//imports
import React, { Component } from 'react';

//start of class
export class Create extends Component {

    //cnstructor for the form
    constructor(props) {
        super(props);
        this.state = { Title: '' };

        //method for change
        this.handleChange = this.handleChange.bind(this);
        //method for submit
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    //handle changes
    handleChange(e) {
        this.setState({ Title: e.target.value });
    }

    //handle the submits
    handleSubmit(e) {
        alert('A movie was submitted: ' + this.state.Title);
        e.preventDefault();
    }

    render() {
        return <div className="App-intro">
            <h1>Hello from create</h1>
            {/* form to store a movie */}
            <form onSubmit={this.handleSubmit}> {/* calling method handleSubmit */}
                <label>
                    Movie Title:
          <input type="text"
                        value={this.state.value}
                        onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />
            </form>
        </div>;
    }
}

export default Create;

