//imports
import React, { Component } from 'react';

//start of class
export class Create extends Component {

    //cnstructor for the form
    constructor(props) {
        super(props);
        this.state = { Title: '', Year , Poster };

        //method for change
        this.handleChange = this.handleChange.bind(this);
        //method for submit
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    //handle changes
    handleChangeTitle(e) {
        this.setState({ Title: e.target.value });
    }
    handleChangePoster(e) {
        this.setState({ Poster: e.target.value });
    }
    handleChangeYear(e) {
        this.setState({ Year: e.target.value });
    }

    //handle the submits
    handleSubmit(e) {
        alert('A movie was submitted: ' + this.state.Title);
        e.preventDefault();
    }

    render() {
        return <div className="App-intro">
            <h1>Hello from create</h1>
            {/* form to store a movie */}
            <form onSubmit={this.handleSubmit}> {/* calling method handleSubmit */}
                <label>
                    Movie Title:
          <input type="text"
                        value={this.state.Title}
                        onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />

                <label>
                   Year:
                <input type="text"
                        value={this.state.Year}
                        onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />
                <div classname = "Form-Group">
                <label>
                 Poster URL:
                <input type="text"
                        value={this.state.Poster}
                        onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />
                </div>
            </form>
        </div>;
    }
}

export default Create;

