//imports
import React, { Component } from 'react';

//start of class
export class Create extends Component {

    //cnstructor for the form
    constructor(props) {
        super(props);
        this.state = { Title: '', Year: '', Poster: '' };

        //method for changes
        this.handleChangeTitle = this.handleChangeTitle.bind(this);
        this.handleChangeYear = this.handleChangeYear.bind(this);
        this.handleChangePoster = this.handleChangePoster.bind(this);
        //method for submit
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    //handle changes
    handleChangeTitle(e) {
        this.setState({ Title: e.target.value });
    }
    handleChangePoster(e) {
        this.setState({ Poster: e.target.value });
    }
    handleChangeYear(e) {
        this.setState({ Year: e.target.value });
    }

    //handle the submits
    handleSubmit(e) {
        alert('A movie was submitted: ' + this.state.Title + this.state.Poster + this.state.Year);
        e.preventDefault();
    }

    render() {
        return <div className="App-intro">
            <h1>Hello from create</h1>
            {/* form to store a movie */}
            <form onSubmit={this.handleSubmit}> {/* calling method handleSubmit */}

                <div classname="Form-Group">
                    <label>
                        Movie Title:
                    </label>
                    <input type="text"
                        classname="Form-Control"
                        value={this.state.Title}
                        onChange={handleChangeTitle} />
                </div>
                <div classname="Form-Group">
                    <label>
                        Year:
                <input type="text"
                            classname="Form-Control"
                            value={this.state.Year}
                            onChange={handleChangeYear} />
                    </label>
                    <input type="text" value="Submit" />
                </div>



                <div classname="Form-Group">
                    <label>
                        Poster:

                    </label>
                    <textarea>
                        rows="6"
                        classname="Form-Control"
                        value={this.state.Poster}
                        onChange={handleChangePoster} </textarea>

                </div>
                <input type="submit" value="Submit" />
            </form>
        </div>;
    }
}

export default Create;

